Script.core:
vec4 fvAmbient  = vec4(0.36, 0.36, 0.36, 1.0);
//vec4 fvSpecular = vec4(0.7,  0.7,  0.7,  1.0);
vec4 fvSpecular = vec4(0.3,  0.3,  0.3,  1.0);
vec4 fvDiffuse  = vec4(0.5,  0.5,  0.5,  1.0);
//float fSpecularPower = 25.0;
float fSpecularPower = 10.0;

uniform sampler2D texture;

varying in float cylR1;
varying in float cylR2;
varying in vec3 cylDir;
varying in vec3 cylP0;
varying in vec3 cylP1;

varying in vec3 ViewDirection;
varying in vec3 fvObjectPosition;
varying in vec3 MVPos;
varying in vec3 Normal;
varying in vec2 texCoord;

float solveEq(float A, float B, float C) {
   	float D = B*B-4.0*A*C;
   	if (D <= 0.0) discard; // no solution/intersection
   	D = sqrt(D);
   	float t1 = (-B+D)/A*0.5;
   	float t2 = (-B-D)/A*0.5;
   	return min(t1, t2);
}

vec3 raycastCylinder() {
   	vec2 tc = 2.0*texCoord - vec2(1.0); // from -1 to 1
   	vec3 rayStart = MVPos;
   	vec3 rayDir = -MVPos;
   	rayDir = normalize(rayDir);

   	// ray cylinder intersection: AtÂ² + Bt + C = 0
   	vec3 rayDRad = rayDir - dot(rayDir, cylDir)*cylDir;
   	vec3 rayPRad = rayStart-cylP0 - dot(rayStart-cylP0, cylDir)*cylDir;
   	float A = dot(rayDRad,rayDRad);
   	float B = 2.0*dot(rayDRad, rayPRad);
   	float C = dot(rayPRad, rayPRad) - cylR1*cylR1;
   	float t = solveEq(A, B, C);
   	vec3 pC = rayStart + t*rayDir;
   	Normal = normalize(rayPRad);

   	// caps
   	// TODO

	return pC;
}

void main( void ) {
   vec3 I = raycastCylinder();
	//vec3 I = IntersectCylinder(vec3(0), -fvObjectPosition);
	//vec3 I = IntersectCylinder(fvObjectPosition, -fvObjectPosition);

   vec3  fvNormal         = normalize( Normal );
   vec3  fvLightDirection = normalize( gl_LightSource[0].position.xyz - I.xyz);
   float fNDotL           = dot( fvNormal, fvLightDirection );

   vec3  fvReflection     = normalize( ( ( 2.0 * fvNormal ) * fNDotL ) - fvLightDirection );
   vec3  fvViewDirection  = normalize( ViewDirection );
   float fRDotV           = max( 0.0, dot( fvReflection, fvViewDirection ) );

   vec4  fvBaseColor      = texture2D(texture, texCoord);

   vec4  fvTotalAmbient   = fvAmbient * fvBaseColor;
   vec4  fvTotalDiffuse   = fvDiffuse * fNDotL * fvBaseColor;
   vec4  fvTotalSpecular  = fvSpecular * ( pow( fRDotV, fSpecularPower ) );

   gl_FragColor = fvTotalAmbient + fvTotalDiffuse + fvTotalSpecular;
   //gl_FragColor = vec4(fvObjectPosition.xyz, 1.0);
   //gl_FragColor = vec4(10*cylR1, 10*cylR2, 0.0, 1.0);
   //gl_FragColor = vec4(abs(cylDir.xyz), 1.0);
   //gl_FragColor = vec4(abs(cylP0.xyz), 1.0);
   //gl_FragColor = vec4(abs(I.xyz), 1.0);
}

//----------------------------------------------------------------------------------------------MAIN--GP
#version 150
#extension GL_EXT_geometry_shader4 : enable
layout (lines) in;
layout (triangle_strip, max_vertices = 24) out;
in vec2 tc[];
in vec3 vert_normal[];

//Phong
out vec3 ViewDirection;
out vec3 fvObjectPosition;
out vec3 MVPos;
out vec3 Normal;
out vec2 texCoord;

out float cylR1;
out float cylR2;
out vec3 cylDir;
out vec3 cylP0;
out vec3 cylP1;

uniform vec3 OSGCameraPosition;

in mat4 mMVv[];
in mat4 mPv[];
in mat3 mNv[];

#define mMVP mPv[0]*mMVv[0]
#define mMV mMVv[0]
#define mP mPv[0]
#define mN mNv[0]

// TODO: not working??
//uniform mat4 OSGModelViewProjectionMatrix;
//uniform mat3 OSGNormalMatrix;
//#define mMVP OSGModelViewProjectionMatrix
//#define mN OSGNormalMatrix

//PHONG-----------------
void addPhongVars(vec4 p, vec3 n, vec2 tc) {
   MVPos = p.xyz;
   fvObjectPosition = vec3(mP*p);
   ViewDirection = -fvObjectPosition.xyz;
   ViewDirection = normalize(ViewDirection);
   Normal = mN * n;
   texCoord = tc;
}

void emitTriangle(vec4 p1, vec4 p2, vec4 p3, vec3 n1, vec3 n2, vec3 n3, vec2 tc1, vec2 tc2, vec2 tc3) {
   gl_Position = mP*p1;
   addPhongVars(p1, n1, tc1);
   EmitVertex();
   gl_Position = mP*p2;
   addPhongVars(p2, n2, tc2);
   EmitVertex();
   gl_Position = mP*p3;
   addPhongVars(p3, n3, tc3);
   EmitVertex();
   EndPrimitive();
}

void emitQuad(vec4 p1, vec4 p2, vec4 p3, vec4 p4, vec3 n1, vec3 n2, vec3 n3, vec3 n4, vec2 tc1, vec2 tc2, vec2 tc3, vec2 tc4) {
   emitTriangle(p1, p2, p4, n1, n2, n4, tc1, tc2, tc4);
   emitTriangle(p2, p4, p3, n2, n4, n3, tc2, tc4, tc3);
}

void emitSimpleQuad(vec4 pos[4], vec2 offs) {
   vec3 norm = cross(vec3(pos[1]-pos[0]),vec3(pos[1]-pos[2]));
   norm *= offs[0]*offs[1];
   float ty1 = 0.5*(offs[0]*offs[1]+1.0);
   float ty2 = 1.0 - ty1;
   for (int i=0;i<4;i++) pos[i] = mMV*pos[i];
   emitQuad(pos[0], pos[1], pos[2], pos[3], norm, norm, norm, norm, vec2(0,ty1), vec2(1,ty1), vec2(1,ty2), vec2(0,ty2));
}

float Sign(in float x) {
    return step(0, x)*2 - 1;
}

vec2 getOffsets() {
   vec3 d = OSGCameraPosition - gl_PositionIn[1].xyz;
   float sd0 = Sign(d[0]);
   float sd1 = Sign(d[2]);
   //int ls = int(step(0, d[0]));
   return vec2(sd0, sd1);
}

void main() {
   vec4 p1 = gl_PositionIn[0];
   vec4 p2 = gl_PositionIn[1];

   cylR1 = tc[0][0]*0.05;
   cylR2 = tc[1][0]*0.05;
   cylDir = vec3(mMV * vec4(normalize(p2.xyz - p1.xyz),0.0));
   cylP0 = vec3(mMV * p1);
   cylP1 = vec3(mMV * p2);
   
   gl_FrontColor = vec4(0.6, 0.6, 0.3, 1.0);

   vec2 offs = getOffsets();
   vec4 pos[4];
   float S1x = 0.05*offs[0];
   float S2x = -0.05*offs[0];
   float S1y = 0.05*offs[1];
   float S2y = -0.05*offs[1];

   pos[0] = p1+vec4(S1x,0,S1y,0)*tc[0][0];
   pos[1] = p2+vec4(S1x,0,S1y,0)*tc[1][0];
   pos[2] = p2+vec4(S2x,0,S1y,0)*tc[1][0];
   pos[3] = p1+vec4(S2x,0,S1y,0)*tc[0][0];
   emitSimpleQuad(pos, offs);

   pos[0] = p2+vec4(S1x,0,S1y,0)*tc[1][0];
   pos[1] = p1+vec4(S1x,0,S1y,0)*tc[0][0];
   pos[2] = p1+vec4(S1x,0,S2y,0)*tc[0][0];
   pos[3] = p2+vec4(S1x,0,S2y,0)*tc[1][0];
   emitSimpleQuad(pos, offs);
}

// vertex shader program
varying vec2 tc;
varying vec3 vert_normal;
varying mat4 mMVv;
varying mat4 mPv;
varying mat3 mNv;

attribute vec4 osg_Vertex;
attribute vec3 osg_Normal;
attribute vec2 osg_MultiTexCoord0;

//----------------------------------------------------------------------------------------------MAIN--VP
void main( void ) {
    tc = osg_MultiTexCoord0.xy;
    gl_Position = osg_Vertex;
    vert_normal = osg_Normal;
    mMVv = gl_ModelViewMatrix;
    mPv = gl_ProjectionMatrix;
    mNv = gl_NormalMatrix;
}
