Script.core:
import VR
r = VR.getRoot().find('Headlight') if hasattr(VR, 'test'): print 'destroy' VR.test.destroy() VR.test = VR.Object('test') r.addChild(VR.test)
def doParticles(): p = VR.Particles(42) # test setter p.setRadius(0.02, 0.01) p.setRadius(0.02) p.setMass(0.1) p.setMass(0.3, 0.1) p.setMassByRadius(1000.0) p.setMassForOneLiter(1000.0) num = p.spawnCuboid(0,15,0, "size",0.2,10,0.2) print num VR.test.addChild(p)
def doGround(): ground = VR.Geometry('ground') ground.setPrimitive('Box 1 1 1 1 1 1') #ground.setColors([255,255,255]) #mat = VR.Material() #mat.setSpecular([250,250,250]) #ground.setMaterial(mat) VR.test.addChild(ground)
ground.translate(0,-1,0) ground.setCollisionMargin(0.01) ground.physicalize(1,0,0)
doGround() doParticles()
